mapper.insert:
INSERT INTO tbl_quotation (
     QUOTATION_REQ_NO
     , CUST_ID
     , PICKUP_ADDR
     , DELIVERY_ADDR
     , MOVE_DT
     , QUOTATION_AMOUNT
     , BUILDING_TYPE
     , ROOM_STRUCTURE
     , HOUSE_SIZE
     , HAS_ELEVATOR
     , BOX_COUNT
     , CID
 )
 VALUES (
     #{quotationReqNo}
     , #{custId}
     , #{pickupAddr}
     , #{deliveryAddr}
     , #{moveDt}
     , #{quotationAmount}
     , #{buildingType}
     , #{roomStructure}
     , #{houseSize}
     , #{hasElevator}
     , #{boxCount}
     , #{cid}
 )

mapper.update:
UPDATE tbl_quotation
SET 
     PICKUP_ADDR = #{pickupAddr}
    , DELIVERY_ADDR = #{deliveryAddr}
    , MOVE_DT = #{moveDt}
    , QUOTATION_AMOUNT = #{quotationAmount}
    , BUILDING_TYPE = #{buildingType}
    , ROOM_STRUCTURE = #{roomStructure}
    , HOUSE_SIZE = #{houseSize}
    , HAS_ELEVATOR = #{hasElevator}
    , BOX_COUNT = #{boxCount}
    , UID = #{cid}
    , UDT = CURRENT_TIMESTAMP()
    , version = version + 1
WHERE QUOTATION_REQ_NO = #{quotationReqNo} AND version = #{version}

UPDATE tbl_quotation
SET status = #{status}
WHERE QUOTATION_REQ_NO = #{quotationReqNo}
