mapper.select:
SELECT user_id
FROM tbl_users 
WHERE user_id = #{userId}

mapper.update:
UPDATE tbl_users
SET username = #{userName},
    password = #{password},
    phone_number = #{phoneNumber},
    email = #{email}
    user_type = #{userType}
WHERE user_id = #{userId}

mapper.select:
SELECT tbl_user_id
FROM tbl_users
WHERE 1=1
AND username = #{userName}
AND email = #{email}

mapper.update:
UPDATE tbl_users
SET password = #{password}
WHERE 1=1
AND user_id = #{userId}
